import React from "react";
import './newsletter.css';

import { Container, Row, Col } from "reactstrap";
import maleTourist from '../assets/images/male-tourist.png';

const Newsletter = () => {   
    return (
        <>
        <section className="newsletter">
            <Container>
                <Row>
                    <Col lg="6">
                        <div className="newsletter__content">
                            <h2>Subscribe now, get amazing discounts and more information.</h2>

                            <div className="newsletter__input">
                                <input type="email" placeholder="Enter Your Email" />
                                <button className="btn newsletter__btn">Subscribe</button>
                            </div>

                            <p>lorem ipsum dolor sit amet consecter adipiscing elit.
                            obcaecati autem, cumque, voluptatibus, eveniet, facere ipsam.</p>
                        </div>
                    </Col>
                    <Col lg="6">
                        <div className="newsletter__img">
                            <img src={maleTourist} alt="cutie" />
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
        </>
    );
};

export default Newsletter;
