import React from "react";
import './common-section.css';
import { Container, Row, Col } from "reactstrap";

const CommonSection = ({ title }) => {
    return (
        <section className="common__section">
            {/* Conditionally render video background if videoSrc is provided */}
            {videoSrc && (
                <video autoPlay loop muted playsInline className="common__section-bg-video">
                    <source src={videoSrc} type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            )}
            {/* Optional: Add an overlay for better text readability over video */}
            {videoSrc && <div className="common__section-overlay"></div>}

            <Container className="text-center">
                <h1>{title}</h1>
            </Container>
        </section>
    );
};

export default CommonSection;
