import galleryImages from "./galleryImages";    
import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";
import './masonaryimagesgallery.css'; 

const MasonryImagesGallery = () => {
    return (
        // You can add a wrapper div here if you need overall padding or a specific background for the gallery section
        // <div className="image-gallery-section-wrapper">
        <ResponsiveMasonry columnsCountBreakPoints={{350: 1, 768: 3, 992: 4}}>
            <Masonry gutter="1.2rem"> {/* Increased gutter slightly for more spacing */}
                {galleryImages.map((item, index) => (
                    <div className="masonry__img-container" key={index}>
                        <img
                            className="masonry__img" // Class on img is still useful
                            src={item}
                            alt={`Travel gallery image ${index + 1}`} // More descriptive alt text
                            style={{ width: "100%", display: "block" }} // Keep these essential styles
                        />
                        <div className="overlay">
                            {/* You can use an icon font here if available, e.g., <i className="ri-eye-line"></i> */}
                        </div>
                    </div>
                ))}
            </Masonry>
        </ResponsiveMasonry>
        // </div>
    );
};

export default MasonryImagesGallery;
