import { Container, Row, Col, Form,  FormGroup, Button } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import "../styles/login.css";
import loginPageBgImage from "../assets/images/your-chosen-login-bg.jpg"; 
import userIcon from "../assets/images/user.png";
import { useState, useContext } from "react";
import { AuthContext } from "./../context/AuthContext";
import { BASE_URL } from "./../utils/config";

const Login = () => {

    const [credentials, setCredentials] =  useState({
       email: undefined,
    password: undefined,
    });

    const {dispatch} = useContext(AuthContext);
    const navigate = useNavigate();

    const handleChange = e => {
        setCredentials( prev => ({ ... prev, [e.target.id]: e.target.value }));
    };

    const handleClick = async e => { 
        e.preventDefault();

        dispatch({type: "LOGIN_START"});

        try {

            const res = await fetch(`${BASE_URL}/auth/login`,{
                method: "post",
                headers: {
                    "content-type":"application/json",
                },
                credentials: "include",
                body: JSON.stringify(credentials),
            });

            const result = await res.json();
            if(!res.ok) alert(result.message);

            console.log(result.data);

            dispatch({type: "LOGIN_SUCCESS", payload:result.data});
            navigate("/");
            
        } catch (err) {
            dispatch({type: "LOGIN_FAILURE", payload:err.message});  
        }
    };

return (
        <section className="auth-section"> {/* Ensure .auth-section styles are in login.css for padding-top */}
            <Container fluid className="h-100">
                <Row className="h-100 justify-content-center align-items-center">
                    <Col lg="10" md="10" className="px-0"> {/* Added px-0 to Col to avoid double padding with container */}
                        <div className="auth__container"> {/* This is the main card-like container */}
                            {/* Left Image Panel */}
                            <div className="auth__img-panel" style={{ backgroundImage: `url(${loginPageBgImage})` }}>
                                <div className="auth__img-overlay-content">
                                    <h1>Find your next adventure.</h1>
                                    <p>Discover amazing places at exclusive deals.</p>
                                    {/* Optional: Link to Register page */}
                                    {/* 
                                    <Button tag={Link} to="/register" outline color="light" size="sm" className="mt-2">
                                        Sign Up Now
                                    </Button> 
                                    */}
                                </div>
                            </div>

                            {/* Right Form Panel */}
                            <div className="auth__form-panel">
                                <div className="form__content-wrapper">
                                    <h3>Hi, good to have you back.</h3>
                                    <Form onSubmit={handleClick}>
                                        <FormGroup>
                                            {/* Using reactstrap Input for consistency if desired, or keep plain input */}
                                            <input 
                                                type="email" 
                                                placeholder="Email address" 
                                                required 
                                                id="email" 
                                                onChange={handleChange} 
                                                value={credentials.email || ""} 
                                            />
                                        </FormGroup>
                                        <FormGroup className="password-group">
                                            <input 
                                                type="password" 
                                                placeholder="Password" 
                                                required 
                                                id="password" 
                                                onChange={handleChange} 
                                                value={credentials.password || ""}
                                            />
                                            {/* Optional: Forgot password link */}
                                            {/* <Link to="/forgot-password"className="forgot-password-link">Forgot password?</Link> */}
                                        </FormGroup>
                                        
                                        {/* Ensure authLoading and authError are available from your AuthContext if you use them */}
                                        <Button 
                                            className="btn auth__btn" // Use only 'auth__btn' if .primary__btn is not desired here or handle in CSS
                                            type="submit" 
                                            // disabled={authLoading} // Uncomment if authLoading is from your context
                                        >
                                            {/* {authLoading ? "Logging in..." : "Login"} // Uncomment if authLoading is from your context */}
                                            Login 
                                        </Button>
                                        
                                        {/* Ensure authError is available from your AuthContext if you use it */}
                                        {/* {authError && <p className="error__message mt-3">{authError}</p>} // Uncomment if authError is from context */}
                                    </Form>
                                    <div className="alternative-action">
                                        <p>Don't have an account? <Link to="/register">Create account</Link></p>
                                    </div>
                                    {/* Optional: Social Login Buttons */}
                                    {/* 
                                    <div className="social-login mt-3">
                                        <p className="text-center or-divider"><span>OR</span></p>
                                        <Button outline block className="mb-2 social-btn social-google"><i className="ri-google-fill me-2"></i>Login with Google</Button>
                                        <Button outline block className="social-btn social-facebook"><i className="ri-facebook-fill me-2"></i>Login with Facebook</Button>
                                    </div>
                                    */}
                                </div>
                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default Login;
